import React, { useEffect, useState } from 'react'
import L from 'leaflet';
import { MapContainer, TileLayer, Marker, Popup, GeoJSON, useMap } from 'react-leaflet'
import mapDefault from './mapDefault.json'
import markerIconPng from "leaflet/dist/images/marker-icon.png"
import {Icon} from 'leaflet'
import { useHistory } from 'react-router-dom';
import { getProvince } from '../../../api/province';

function Info() {
	let history = useHistory()
  const map = useMap()
  
  const [provinceList, setProvinceList] = useState([])

  const fetchData = React.useCallback(() => {
        getProvince()
        .then((response) => {
            setProvinceList(response.data)
        
        console.log('setProvinceList ', response.data)
        })
        .catch((error) => {
        console.log(error)
        })

    }, [])
    React.useEffect(() => {
    fetchData()
    }, [fetchData])
  
  const handleProvince = (prov) => {
    console.log(provinceList)
    for (let i = 0; i < provinceList.length; i++) {
      
      if (prov.toLowerCase() === provinceList[i].nom.toLowerCase()) {
        
        console.log(prov.toLowerCase())
      } 
    }
  }
  
  useEffect(() => {

      
    var div;
    
    function getColor(d) {
      return  d > 400 ? '#cb181d' :
      d > 300 ? '#fb6a4a' :
      d > 200  ? '#fcae91' :
      d > 100  ? '#fee5d9' :
                  '#fff7fb';
    
    }

    if (map) {
      const info = L.control();

      // 

      function style(feature) {
        
        return {
          weight: 2,
          opacity: 1,
          color: 'white',
          dashArray: '3',
          fillOpacity: 0.7,
          // fillColor: getColor(casPositif)
          fillColor: getColor(feature.properties.liveCases)
        };
      }

      info.onAdd = () => {
        div = L.DomUtil.create("div", "info infos" );
        info.update()
        var x = document.getElementsByClassName("info infos leaflet-control")[0];
        if (typeof(x)!== 'undefined') {
          
          x.remove()
          // console.log("x is..",x)
        }
        return div;
      };

      info.update= function(props){
        
        // div.innerHTML = `<h4>RD CONGO</h4>
        // <div style="color: #000000"><b>
        //   ${props.name}</b><br />
        // </div>`

        
          div.innerHTML = '<h4>RD CONGO</h4>' +  (props ?
        '<b>' + props.name + '</b>' 
        : 'Survolez une province ');
      }

      
      function highlightFeature(e) {
        var layer = e.target;

        layer.setStyle({
          weight: 5,
          color: '#666',
          dashArray: '',
          fillOpacity: 0.7
        });

        if (!L.Browser.ie && !L.Browser.opera && !L.Browser.edge) {
          layer.bringToFront();
        }

        info.update(layer.feature.properties);
      }

    var geojson;
    function resetHighlight(e) {
      geojson.resetStyle(e.target);
      info.update();
    }

	function zoomToFeature(e) {
    var layer = e.target;
		map.fitBounds(e.target.getBounds());
    // setVilleData(layer.feature.properties.ville)
    // console.log("layer is...", layer.feature.properties.name)
    handleProvince(layer.feature.properties.name) 
    // console.log("provinces geojson", provsDatas)
    
    // dispatch(setCitiesDatas(layer.feature.properties.ville))
    // showMarker()
    // history.push('/') 
    
	}

	function onEachFeature(feature, layer) {
		layer.on({
			mouseover: highlightFeature,
			mouseout: resetHighlight,
			click: zoomToFeature
		});
	}


      info.addTo(map);
      geojson = L.geoJson(mapDefault, {
        style: style,
        onEachFeature: onEachFeature
    }).addTo(map);

    }
    
  console.log(map)
  }, [map]); //here add map
  const showMarker=()=>(
    
    <Marker 
    position={[-4.3217055, 15.3125974]} 
      icon={new Icon({iconUrl: markerIconPng, iconSize: [25, 41], iconAnchor: [12, 41]})} 
    >
      <Popup>
        A pretty CSS3 popup. <br /> Easily customizable.
      </Popup>
    </Marker>
  )
  
  console.log(map)
  return null;
}

export default Info
